import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { animate, group, query, style, transition, trigger } from '@angular/animations';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
  animations: [
    trigger('routeAnim', [
      transition(':increment', [
        style({
          position: 'relative',
          overflow: 'hidden'
        }),


        query(':enter, :leave', [
          style({
            // display: 'block',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%'
        })
        ], {optional:true}),

      //  query(':enter', [
       //   style({ opacity: 0 })
       // ], { optional:true }),

        group([
          query(':leave', [
            animate('200ms ease-in', style({
              opacity: 0,
              transform: 'translateX(-50px)'
            }))
          ], { optional: true }),


          query(':enter', [
            style ({
              transform: 'translateX(50px)'
            }),
            animate('250ms 120ms ease-out', style({
              opacity: 1,
              transform: 'transalteX(0)'
            }))
          ], { optional: true })
        ])

      ]),

      transition(':decrement', [
        style({
          position: 'relative',
          overflow: 'hidden'
        }),


        query(':enter, :leave', [
          style({
            // display: 'block',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%'
        })
        ], {optional:true}),

       // query(':enter', [
        //  style({ opacity: 0 })
       // ], { optional:true }),

        group([
          query(':leave', [
            animate('200ms ease-in', style({
              opacity: 0,
              transform: 'translateX(50px)'
            }))
          ], { optional: true }),


          query(':enter', [
            style ({
              transform: 'translateX(-50px)'
            }),
            animate('250ms 120ms ease-out', style({
              opacity: 1,
              transform: 'transalteX(0)'
            }))
          ], { optional: true })
        ])

      ])
    ])
  ]
})
export class AppComponent {

  bg: string = 'https://pbs.twimg.com/media/D3Q4KJbWkAIZBAi?format=jpg&name=4096x4096'

  prepareRoute(outlet: RouterOutlet) {
    if (outlet.isActivated) return outlet.activatedRouteData['tab']
  }

  async changeBGImage() {
  const result = await fetch('https://picsum.photos/', {
      method: 'HEAD'
    })

    if (result.url === this.bg) return this.changeBGImage()

    this.bg = result.url
    return this.changeBGImage 
  }

}
<div class="app-wrapper">

  <img [src]="bg" class="bg-img">

  <div class="btn change-bg-btn" (click)="changeBGImage()">
    <i class="material-icons">refresh</i>
  </div>

<div class="top-section">
  <h1>08:00</h1>
  <h2>1 March 2023</h2>
</div>

  <div class="content-section" [@routeAnim]="prepareRoute(outlet)">
    <router-outlet #outlet="outlet"></router-outlet>
  </div>

  <div class="bottom-section">
    <app-tabs></app-tabs>
  </div>

  <app-notification></app-notification>

</div>
